
public class 반복문_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1~10 까지 출력 
		for(int i=1;i<=10;i++)
		{
			System.out.println(i+" ");
		}
		System.out.println();
		for(int i=10;i>=1;i--)
		{		
			System.out.println(i+" ");
		}
		System.out.println();
		for(char i='a';i<='Z';i++)
			System.out.println(i);
		System.out.println();
		System.out.println();
		for(char i='Z';i>='A';i--)
			System.out.println(i);
		
	}

}
